content:
body:has(#bg1) {

  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;

  --nav-bar-width: 50px;
  --char-panel-with: 0px;
  --settings-panel-width: 0px;
  --authors-note-panel-width: 0px;
  --expression-image-width: 0px;
  --cfgConfig-panel-width: 0px;
  --logprobsViewer-panel-width: 0px;
  --groupMemberListPopout-panel-width: 0px;
  --stcdxcodex: 0px;
  --gallery-panel-width: 0px;
  --avatar-base-width: 50px;
  --avatar-base-height: 50px;
  --drawer-content-panel-width:         calc( 100vw - var(--nav-bar-width) - var(--settings-panel-width) - var(--char-panel-width) - var(--authors-note-panel-width) - var(--stcdxcodex) - var(--groupMemberListPopout-panel-width) - var(--gallery-panel-width) - (var(--theme-margin) * 2) - (var(--theme-margin) / 4));
  --drawer-content-panel-wit-img-width: calc( 100vw - var(--nav-bar-width) - var(--cfgConfig-panel-width) - var(--expression-image-width) - var(--authors-note-panel-width) - var(--char-panel-width) - var(--settings-panel-width) - var(--stcdxcodex) - var(--groupMemberListPopout-panel-width) - var(--gallery-panel-width) - (var(--theme-margin) * 2) - (var(--theme-margin) / 4));
  --theme-margin: 12px;

  scrollbar-color: var(--SmartThemeUnderlineColor) var(--SmartThemeBlurTintColor) !important;
  background-color: var(--SmartThemeBorderColor);
  display: flex;

  --black50a: color-mix(in srgb, var(--SmartThemeBorderColor) 30%, transparent);
  --white50a: color-mix(in srgb, var(--SmartThemeUnderlineColor) 60%, rgba(255, 255, 255, 0.5));
}

*{
  transition: none;
}

.drawer-icon.closedIcon{

  opacity: 0.6;
}

hr {
  background-image: unset !important;
  margin: 5px 0;
  height: 1px;
  min-height: 1px;
  border: 0;
  opacity: 0.2;
}

.drawer-icon {
  text-shadow: 0px 0px calc(var(--shadowWidth) * 1px) var(--SmartThemeShadowColor);
}

#form_character_search_form {
gap: 0.2rem;
}

.disabledWIEntry {
  opacity: 1;
  filter: none;
}

#extensionTopBar {
border-radius: unset !important;
background-color: var(--SmartThemeBlurTintColor) !important;
border: unset !important;
}

#extensionTopBarSearchInput:placeholder-shown {
border-color: var(--SmartThemeBlurTintColor);
}

.options-content{
  border: unset;
}

#options {
  background-color: var(--SmartThemeBlurTintColor) !important;

}

body.charListGrid #rm_print_characters_block .character_select .character_select_container {
  max-width: unset !important;
  overflow: hidden;
}

body.bubblechat .mes {
  padding: 6px;
  border-radius: unset;
  margin-bottom: 0px;
  border: unset !important;
}

.wi-card-entry {
  border:unset !important;
  border-radius: unset;
  padding: 1px;
}

.inline-drawer-header{
  background: var(--black50a);
  padding: 0px 6px 0px 6px;
}

.title_restorable{
  border-radius: 8px 8px 0px 0px !important;
  background:unset !important;
}

#CustomCSS-block,
#UI-presets-block{
background: var(--black50a);
display: flex;
border-radius: 8px;
margin: 4px 0px 12px 0px;
padding: 2px;
}

#customCSS{
  border-radius: 0px 0px 8px 8px !important;
}
.inline-drawer-header:has(.up) {
  border-radius: 8px 8px 0px 0px;
}
.inline-drawer-header:has(.down) {
  border-radius: 6px;
}
.inline-drawer-content {
  padding: 5px;
  background: var(--black50a);
  border-radius: 0px 0px 8px 8px;
}

#charListFixedTop {
  display: none;
  transform-origin: left top;
  border-radius: 8px;
}

#right-nav-panel:has(#rm_PinAndTabs:hover, #rm_PinAndTabs:focus-within) #charListFixedTop,
#charListFixedTop:hover,
#charListFixedTop:focus-within {
  display: block;
  border-radius: 0px 0px 8px 8px ;
}
#right-nav-panel:has(#charListFixedTop:hover, #charListFixedTop:focus-within) #rm_PinAndTabs{
  display: block;
  border-radius: 8px 8px 0px 0px ;
}

#right-nav-panel:has(#rm_ch_create_block:is([style*="display: block; opacity: 1;"])) #rm_PinAndTabs
{
  border-radius: 8px 8px 0px 0px !important;
}
#rm_PinAndTabs:hover{
  border-radius: 8px 8px 0px 0px;
}

#rm_button_selected_ch h2::before {
  text-align: center;
  content: "\f13a";
  margin-right: 6px;
  line-height: 10%;
  font-family: "Font Awesome 6 Free";
}

#right-nav-panel:has(#rm_ch_create_block:is([style*="display: block; opacity: 1;"])) #rm_button_selected_ch h2::before {
  content: ""!important;
}

#right-nav-panel:has(#rm_PinAndTabs:hover, #rm_PinAndTabs:focus-within, #charListFixedTop:hover, #charListFixedTop:focus-within) #rm_button_selected_ch h2::before {
  content: "\f139" ;
}


#rm_PinAndTabs {
  border-radius: 8px;
  background-color: var(--SmartThemeBlurTintColor);

}

#charListFixedTop{
  border-radius: 0px 0px 8px 8px;
  background-color: var(--SmartThemeBlurTintColor);
  padding: 6px;
  margin: 0px;
}

#rm_ch_create_block{
  border-radius: 0px 0px 8px 8px;
  background-color: var(--SmartThemeBlurTintColor);
  padding: 6px;
  margin: 0px;
}

#rm_button_selected_ch h2{
  margin: 2px;
}

.standoutHeader {
  border-top-left-radius: 6px !important;
  border-bottom-right-radius: 6px !important;
}

#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4, .standoutHeader {
  background: var(--black50a);
  margin: 4px 0px 0px 0px;
  padding: 4px;

  margin-bottom: 0px;
  border-radius: 6px;
  border: unset !important;
}
#extensions_settings .inline-drawer-toggle.inline-drawer-header:has(.up),
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header:has(.up)
{
  border-radius: 6px 6px 0px 0px;
}

select {
  background-color: var(--SmartThemeShadowColor) !important;
  border: none;
  color: var(--SmartThemeBodyColor)!important;
}
select option {
  background-color: var(--SmartThemeBlurTintColor) !important;
  border: none;
  color: var(--SmartThemeBodyColor)!important;
}

input,
select,
.interactable{
  border: unset !important;
  border-radius: 6px;

}
.userSettingsInnerExpandable {
  border: unset !important;
  padding: 2px 5px !important;
  margin: 5px 0 0 0 ;
}


body:has(#stcdx--codex.stcdx--active) #WorldInfo {  
  max-width: var(--drawer-content-panel-width);
}

body.charListGrid:has(#right-nav-panel.openDrawer):not(#rm_ch_create_block) {
  --char-panel-width: 700px;
}

body:has(#right-nav-panel.openDrawer) {
  --char-panel-width: 420px;
}

body:has(#rm_ch_create_block:is([style*="display: block; opacity: 1;"])) {
  --char-panel-width: 420px !important;
}

body:has(.stcdx--root.stcdx--codex.stcdx--active) {
  --stcdxcodex: 400px !important;
}

body:has(#right-nav-panel.closedDrawer) {
  --char-panel-width: 0px !important;
}

body:has(#left-nav-panel.openDrawer) {
  --settings-panel-width: 360px !important;
}

body:has(#expression-image:not([src=""])) {
  --expression-image-width: 512px !important;
}

body:has(#expression-image:not([src=""])).waifuMode {
  --expression-image-width: 0px !important;
}

body:has(.zoomed_avatar.draggable) {
  --expression-image-width: 512px  !important;
}
body:has(.zoomed_avatar.draggable).waifuMode {
  --expression-image-width: 0vw !important;
}

body:not(:has(.zoomed_avatar.draggable)) {
--expression-image-width: 0px;
}

body:has(#floatingPrompt:is([style*="display: flex; opacity: 1;"])) {
  --authors-note-panel-width: 400px;
}

body:has(#floatingPrompt.maximized) {
  --authors-note-panel-width: 0px;
}

body:has(#cfgConfig:is([style*="display: flex; opacity: 1;"])) {
  --cfgConfig-panel-width: 360px;
}

body:has(#cfgConfig.maximized) {
  --cfgConfig-panel-width: 0px;
}

body:has(#groupMemberListPopout) {
  --groupMemberListPopout-panel-width: 340px;
}
body:has(#gallery.draggable) {
  --gallery-panel-width: 340px;
}

body:has(#logprobsViewer:is([style*="display: flex; opacity: 1;"])) {
  --expression-image-width: 20vw !important;
}

body:has(#logprobsViewer.maximized) {
  --expression-image-width: 0px;
}

#top-bar {
  border-bottom: unset;
  background-color: var(--SmartThemeBorderColor);
  margin: unset;
  width: unset;
  height: unset;
  position: unset;
}

textarea,
.text_pole{
  border: unset !important;
  background-color: color-mix(in srgb, var(--SmartThemeShadowColor) 70%, transparent);
}

body.waifuMode #sheld{
  top: unset !important;
  bottom: calc((var(--theme-margin) / 4) - 3px);
}

#rm_ch_create_block
{
  box-shadow: 0 6px 10px 3px #00000080;
}

#charListFixedTop,
#rm_PinAndTabs,
#sheld,
#floatingPrompt,
.drawer-content
{
  box-shadow: 0 0 10px 3px #00000080;
}

.mes_button, .extraMesButtons > div{
  filter: unset !important;
}

#sheld {
  justify-content: left;
  padding: 0px;
  border-radius: 6px;
  height: calc(100svh - (var(--theme-margin) * 2));
  max-height: unset;
  top: 0;
  backdrop-filter: blur(var(--SmartThemeBlurStrength));
  -webkit-backdrop-filter: blur(var(--SmartThemeBlurStrength));

}

#sheld,
#bg1{
  height: calc(100svh - (var(--theme-margin) * 2));
  margin: var(--theme-margin) !important;
  border-radius: 6px !important;
  left: calc(var(--nav-bar-width) + var(--char-panel-width) + var(--cfgConfig-panel-width)) !important;
}


#sheld,
#chat,
#bg1,
#form_sheld {
  padding: 0px;
  max-height: unset;
  width: var(--drawer-content-panel-wit-img-width);
  border: none !important;
  }


#cfgConfig {
left: calc(var(--nav-bar-width) + var(--char-panel-width) + (var(--theme-margin)/4));
width: calc(var(--cfgConfig-panel-width) - var(--theme-margin) );
height: calc(100svh - (var(--theme-margin) * 2));

  z-index: 40 !important;
  max-height: unset;
  border-radius: unset;
  border: unset;
  min-width: unset;
}

#CharListButtonAndHotSwaps {
  padding: 6px;
}
.select2-container .select2-selection--multiple, .select2-container .select2-selection--single,
.select2-container.select2-container--focus .select2-selection--multiple, .select2-container.select2-container--focus .select2-selection--single{
  border: unset;
}

body.charListGrid #rm_print_characters_block {
  padding-right: 10px;
}

#right-nav-panel {
  left: var(--nav-bar-width);
  width: calc(var(--char-panel-width) );
  height: calc(100svh);
  z-index: 10 !important;
  max-height: unset;
  border-radius: unset;
  border: unset;
  min-width: unset;
  padding: 16px 12px;
  margin: 0px 0px 0px 0px !important;

}

#top-settings-holder {
  background-color:unset !important;
  margin: 6px;
  position: unset;
  height: calc(100svh - (var(--theme-margin) * 2));
  flex-direction: column;
  padding: 16px 8px;
  gap: 16px;
  justify-content: unset;
  width: var(--nav-bar-width);


  &::before {
      content: "";
      width: 100%;
      aspect-ratio: 1/1;
      background-image: url("img/five.png");
      background-size: cover;
      background-position: center center;
      border-radius: 50%;
  }
}

.user-settings-v2-block.drawer-content.stcdx--active {
max-width: var(--drawer-content-panel-width);
}

body .stcdx--tooltip {
position: absolute;
bottom: 0;
right: 0;
top: calc(2em);
max-height: unset;
max-width: unset;
height: unset;
width: calc((100vw - var(--sheldWidth)) / 2) !important;
}
#right-nav-panel.drawer-content
{
  background-color: unset !important;
}

.drawer-content select{
  margin: 2px;
}

.drawer-content {
  background-color: var(--SmartThemeBlurTintColor) !important;
  border: unset !important;
  width: var(--drawer-content-panel-width);
  left: calc(var(--nav-bar-width) + var(--char-panel-width));
  margin: var(--theme-margin) !important;
  border-radius: 8px;
  z-index: 20 !important;
  top: 0;
  padding: 12px;
}

#expression-holder {
  right: calc(var(--settings-panel-width) + var(--authors-note-panel-width) + var(--stcdxcodex) + var(--groupMemberListPopout-panel-width) + var(--gallery-panel-width));
  left: unset;
  justify-content: right;
}


#gallery {
  right: calc(var(--settings-panel-width) + var(--authors-note-panel-width) + var(--stcdxcodex));  
  left: unset;
  display: flex;
  position: fixed;
  bottom: unset !important;
  gap: 5px;
  width: var(--gallery-panel-width);
  z-index: 40 !important;
  border-radius: unset;

  max-height: unset;
  box-shadow: none;

}

#groupMemberListPopout {
  right: calc(var(--settings-panel-width) + var(--authors-note-panel-width) + var(--stcdxcodex) + var(--gallery-panel-width));  
  left: unset;
  display: flex;
  position: fixed;
  bottom: unset !important;
  gap: 5px;
  width: var(--groupMemberListPopout-panel-width);
  z-index: 40 !important;
  border-radius: unset;

  max-height: unset;
  box-shadow: none;
}

body.waifuMode:has(#extensionTopBar)  #chat{
  border-radius: 0px !important;
}

body.waifuMode .zoomed_avatar {
  margin: var(--theme-margin) !important;
  padding: 0 !important;
  border: 0 !important;
  border-radius: 6px !important;
  background-color: transparent !important;
  right: calc(var(--settings-panel-width) + var(--authors-note-panel-width) + var(--stcdxcodex) + var(--groupMemberListPopout-panel-width) + var(--gallery-panel-width) + (var(--theme-margin) / 4));
  left: unset;
  z-index: 20 !important;
}

.zoomed_avatar {
  margin: var(--theme-margin) !important;
  Width: calc(var(--expression-image-width) - (var(--theme-margin) / 2));
  height: auto;
  align-items:end !important;
  padding: 0 !important;
  border: 0 !important;
  border-radius: 6px !important;
  background-color: transparent !important;
  left: unset !important;
  right: calc(var(--settings-panel-width) + var(--authors-note-panel-width) + var(--stcdxcodex) + var(--groupMemberListPopout-panel-width) + var(--gallery-panel-width) + (var(--theme-margin) / 4));
  z-index: 30 !important;
}

.zoomed_avatar_container{
  display: flex;
  align-items: end !important;
  margin: unset !important;
  width: 100% !important;
  height: 100% !important;
}

.zoomed_avatar img{
  border-radius: 6px !important;
  width: 100% !important;
  height: 100% !important;
  object-fit: unset !important;
}



.draggable {
  border-radius: 6px;
  z-index: 9999;
}

#logprobsViewer:not(.maximized) {
  right: calc(var(--settings-panel-width) + var(--authors-note-panel-width) + var(--stcdxcodex));  
  z-index: 30 !important;
  left: unset;
  width: var(--expression-image-width);
}

#logprobsViewer.maximized {
   z-index: 50 !important;
}

#expression-wrapper {
  z-index: 20 !important;
  width: 0 !important;
  height: 0 !important;
}

#floatingPrompt:not(.maximized) {
  margin: var(--theme-margin) !important;
  z-index: 40 !important;
  right: calc(var(--settings-panel-width) + (var(--theme-margin) / 3));
  left: unset;
  width: calc(var(--authors-note-panel-width) - (var(--theme-margin) / 2));
  border-radius: 6px;
  height: calc(100svh - (var(--theme-margin) * 2));
  max-height: unset;
}

#qr--popout {
   z-index: 9999 !important;
}

#floatingPrompt.maximized {
  z-index: 50 !important;
}

#left-nav-panel.drawer-content {
  margin: var(--theme-margin) !important;
  right: 0;
  padding: 0;
  left: unset;
  width: calc(var(--settings-panel-width) - (var(--theme-margin) / 4));
  border: unset;
  height: calc(100svh - (var(--theme-margin) * 2));
  max-height: unset;
}

#vv--root.vv--maximized {
  z-index: 50 !important;
  width: var(--drawer-content-panel-width);
  left: calc(var(--nav-bar-width) + var(--char-panel-width));
  margin: var(--theme-margin) !important;
  min-height: 100px !important;
  max-height: calc(100vh - var(--bottomFormBlockSize));
  border: unset;
  bottom: 0;
}

#vv--root {
  z-index: 50 !important;
  width: var(--drawer-content-panel-width);
  left: calc(var(--nav-bar-width) + var(--char-panel-width));
  margin: var(--theme-margin) !important;
  max-width: 100vw !important;
  min-height: 100px !important;
  max-height: calc(100vh - var(--bottomFormBlockSize));
  border: unset;
  bottom: unset;
}

body.no-blur #top-bar, body.no-blur #send_form, #send_form.compact{
  background-color: var(--SmartThemeBlurTintColor) !important;
  border-radius: unset ;
  border: unset;
}
#send_form{
  border: unset;
  border-radius: unset;
}

#qr--settings #qr--set-qrList .qr--set-qrListContents > .qr--set-item > :nth-child(5) {
  flex: inherit;
}

#floatingPrompt, #cfgConfig{
padding: 0px 0px 0px 6px;
padding-top: 30px;
border-radius: 6px;
}

.checkbox_label {
overflow: hidden;
}

.scrollY{
padding-right: 6px;
}

.csss--input::selection, .csss--content::selection {
  color: #ffffff !important;
  background: #ff260071 !important;
}

.bg_list{
width: unset;
}


/*********
  stcdx  
*********/

body .stcdx--root {
  right: calc(var(--settings-panel-width) + var(--authors-note-panel-width) + (var(--theme-margin) / 2));
  background-color: var(--SmartThemeBlurTintColor) !important;
  width: calc(var(--stcdxcodex) - (var(--theme-margin)));
  z-index: 50 !important;
  border-radius: 6px;
  border: unset;
  filter: unset;
  margin: var(--theme-margin) !important;
  filter: none;
  transition: none;
}

body .stcdx--root > .stcdx--content {
transition: unset !important;
}

body .stcdx--codex {
transition: unset !important;
}

body .stcdx--codex > .stcdx--header{
  gap: unset !important;
}

body .stcdx--codex > .stcdx--header > .stcdx--action {
  transition: unset !important;
  opacity: 1;
}

body .stcdx--codex > .stcdx--header > .stcdx--action.stcdx--menu > .stcdx--books {
background-color: var(--SmartThemeBorderColor) ;
}

/*********
 Avatars 
*********/




#rm_print_characters_block .ch_name, .avatar-container .ch_name {
  flex: 1 1 auto;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  display: block;
}

body.charListGrid #rm_print_characters_block .bogus_folder_select, 
body.charListGrid #rm_print_characters_block .character_select, 
body.charListGrid #rm_print_characters_block .group_select, 
#user_avatar_block.gridView .avatar-container {
  height: auto;
  margin: 0px;
  padding: 10px;
  background: linear-gradient(315deg, var(--SmartThemeBlurTintColor) 80%, var(--SmartThemeBorderColor) 98%);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 6px;
}

body #rm_print_characters_block .bogus_folder_select, 
body #rm_print_characters_block .character_select, 
body #rm_print_characters_block .group_select, 
#user_avatar_block .avatar-container {
  height: auto;
  margin: 2px 0px 2px 0px;
  padding: 6px;
  background: linear-gradient(225deg, var(--SmartThemeBlurTintColor) 70%, var(--SmartThemeBorderColor) 98%);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 6px;
}
.avatar img {
  width: 100%;
  border-radius: 6px !important;

  border: unset !important;
}

body.charListGrid .character_select .avatar,
body.charListGrid .group_member .avatar,
body.charListGrid #group_avatar_preview .avatar {
  --big-avatar-height-factor: 2.4 !important;
  --big-avatar-width-factor: 1.6 !important;
  --big-avatar-border-factor: 5 !important;
}

.avatar.inline_avatar.flex.alignitemscenter.textAlignCenter.selectable.character_select {
    --avatar-base-height: 50px !important;
    --avatar-base-width: 50px !important;
    --big-avatar-height-factor: 1 !important;
    --big-avatar-width-factor: 0.8 !important;
    --big-avatar-border-factor: 5 !important;
}

body.big-avatars.charListGrid .character_select .avatar:not(.add_avatar), 
body.big-avatars.charListGrid .avatar_collage {
  align-items: unset;
  margin: 0px;
  overflow: hidden;

  background: linear-gradient(135deg, #2a2a2a 25%, #1e1e1e 75%);
  border-radius: 6px;
  padding: 0px 0px 0px 4px;
}

.character_select.is_fav .avatar, .group_select.is_fav .avatar, .group_member.is_fav .avatar, .avatar.is_fav {
  background: linear-gradient(315deg, rgba(255, 255, 255, 0.1) 0%, #1e1e1e 75%, var(--SmartThemeUnderlineColor) 50%, #2a2a2a 90%) !important;
  border-radius: 6px;
}

.character_select.is_fav .ch_name, .group_select.is_fav .ch_name, .group_member.is_fav .ch_name {
  color: var(--SmartThemeUnderlineColor);
}

.character_select.is_fav .avatar, .group_select.is_fav .avatar, .group_member.is_fav .avatar, .avatar.is_fav{
  outline: unset;
}


.bogus_folder_select, .character_select, .group_select {
  display: flex;
  flex-direction: row;
  align-items: center !important;
